import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';
import { FaTachometerAlt, FaBolt, FaCarCrash, FaMapMarkerAlt } from 'react-icons/fa';
import './Dashboard.scss';

// Importing necessary Chart.js components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]); // Store full GPS data for the route
  const [route, setRoute] = useState([]);     // Coordinates for map polyline
  const [performanceData, setPerformanceData] = useState({
    speed: [],
    acceleration: [],
    jerk: [],
  });
  const [driverId, setDriverId] = useState(null);
  const [error, setError] = useState('');
  const [isMockMode, setIsMockMode] = useState(true);  // Mock GPS for testing

  // Fetch driver data (driver_id) from the backend using JWT token
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/user-data', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDriverId(response.data.driver_id);
      } catch (err) {
        setError('Error fetching user data');
      }
    };
    fetchData();
  }, []);

  // Track GPS data in batches: mock for now
  useEffect(() => {
    if (driverId) {
      const interval = setInterval(() => {
        if (isMockMode) {
          sendMockGpsData();  // Mock GPS data
        } else {
          getRealGpsDataBatch(); // Use real GPS in actual scenarios
        }
      }, 30000); // Send a batch every 30 seconds

      return () => clearInterval(interval);  // Cleanup interval
    }
  }, [driverId, isMockMode]);

  // Send the batch of GPS data to the backend at '/api/record-telematics'
  const sendGpsDataToBackend = async (gpsDataBatch) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/record-telematics',
        {
          driver_id: driverId,
          gps_data: gpsDataBatch, // Batch of GPS data
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      // Process the GPS data and map it
      const gps = response.data.gps_data || [];
      const performance = response.data.performance || { speed: [], acceleration: [], jerk: [] };

      // Track the first and last GPS points of each batch for the map route
      const batchStartLat = gpsDataBatch[0].Latitude;
      const batchStartLon = gpsDataBatch[0].Longitude;
      const batchEndLat = gpsDataBatch[gpsDataBatch.length - 1].Latitude;
      const batchEndLon = gpsDataBatch[gpsDataBatch.length - 1].Longitude;

      setGpsData([...gpsData, ...gps]); // Update full GPS data
      setRoute([...route, [batchStartLat, batchStartLon], [batchEndLat, batchEndLon]]); // Add batch route

      // Set performance data for graphs
      setPerformanceData({
        speed: performance.speed,
        acceleration: performance.acceleration,
        jerk: performance.jerk,
      });
    } catch (err) {
      setError('Error sending GPS data');
    }
  };

  // Call the daily processing at midnight
  const processDailyData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/process-daily-data',
        { driver_id: driverId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('Daily data processed', response.data);
    } catch (err) {
      console.error('Error processing daily data', err);
    }
  };

  // Schedule process-daily-data call at 12 AM every day
  useEffect(() => {
    const midnight = new Date();
    midnight.setHours(24, 0, 0, 0);  // Set time to 12 AM midnight

    const delay = midnight.getTime() - Date.now();  // Time until next midnight
    const timer = setTimeout(() => {
      processDailyData(); // Call daily data processing at midnight
    }, delay);

    return () => clearTimeout(timer);  // Clean up timer
  }, [driverId]);

  // Safely handle gpsData and performanceData before using map()
  const chartData = {
    labels: gpsData?.map((_, index) => `T${index}`) || [],
    datasets: [
      { label: 'Speed (m/s)', data: performanceData?.speed || [], borderColor: 'rgba(75,192,192,1)' },
      { label: 'Acceleration (m/s²)', data: performanceData?.acceleration || [], borderColor: '#FF6384' },
      { label: 'Jerk (m/s³)', data: performanceData?.jerk || [], borderColor: '#36A2EB' },
    ],
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="dashboard-content">
        <div className="map-section">
          <MapContainer center={[40.712776, -74.005974]} zoom={13} className="map">
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Polyline positions={route || []} color="blue" />
          </MapContainer>
        </div>
        <div className="graph-section">
          <Line data={chartData} />
        </div>
      </div>
      <div className="icon-section">
        <div className="icon-item">
          <FaTachometerAlt /> Speed: {performanceData?.speed?.slice(-1)} m/s
        </div>
        <div className="icon-item">
          <FaBolt /> Acceleration: {performanceData?.acceleration?.slice(-1)} m/s²
        </div>
        <div className="icon-item">
          <FaCarCrash /> Jerk: {performanceData?.jerk?.slice(-1)} m/s³
        </div>
        <div className="icon-item">
          <FaMapMarkerAlt /> Total Route Points: {route?.length || 0}
        </div>
      </div>
      <DailyScorePopup driverId={driverId} />  {/* Display the Daily Score Popup */}
    </div>
  );
};

export default Dashboard;
