import React, { useEffect } from 'react';
import './DailyScorePopup.scss';

const DailyScorePopup = ({ dailyData }) => {
  // Ensure that dailyData and aggregated_data exist before rendering
  if (!dailyData || !dailyData.aggregated_data) {
    return <p>No daily data available.</p>;
  }

  // Log the dailyData received by the component
  useEffect(() => {
    console.log('Props passed to DailyScorePopup:', dailyData);
  }, [dailyData]);

  // Destructure the necessary fields from aggregated_data
  const {
    'Speed(km/h)': speedKmh = 'N/A',
    'Acceleration(m/s^2)': acceleration = 'N/A',
    'Jerk(m/s^3)': jerk = 'N/A',
    'Braking_Intensity': brakingIntensity = 'N/A',
    'SASV': sasv = 'N/A',
    'Speed_Violation': speedViolation = 'N/A',
    'Heading_Change(degrees)': headingChange = 'N/A'
  } = dailyData.aggregated_data || {}; // Safe fallback

  // Handle NaN values for jerk
  const formattedJerk = isNaN(jerk) ? 'N/A' : jerk;

  return (
    <div className="daily-score-popup">
      <h3>Daily Driving Summary</h3>
      <div className="summary-container">
        <div className="summary-item">
          <h4>Driving Score</h4>
          <p>{dailyData.driving_score || 'N/A'} / 100</p>
        </div>
        <div className="summary-item">
          <h4>Driving Category</h4>
          <p>{dailyData.driving_category || 'N/A'}</p>
        </div>
        <div className="summary-item">
          <h4>Total Distance Covered</h4>
          <p>{dailyData.total_distance_covered_km || 'N/A'} km</p>
        </div>
        <div className="summary-item">
          <h4>Average Speed</h4>
          <p>{speedKmh} km/h</p>
        </div>
        <div className="summary-item">
          <h4>Acceleration</h4>
          <p>{acceleration} m/s²</p>
        </div>
        <div className="summary-item">
          <h4>Jerk</h4>
          <p>{formattedJerk} m/s³</p> {/* Handle NaN */}
        </div>
        <div className="summary-item">
          <h4>Braking Intensity</h4>
          <p>{brakingIntensity}</p>
        </div>
        <div className="summary-item">
          <h4>Heading Change</h4>
          <p>{headingChange} degrees</p>
        </div>
        <div className="summary-item">
          <h4>Speed Violations</h4>
          <p>{speedViolation}</p>
        </div>
        <div className="summary-item">
          <h4>SASV (Sudden Acceleration/Speed Violation)</h4>
          <p>{sasv}</p>
        </div>
      </div>
    </div>
  );
};

export default DailyScorePopup;
