import React, { useState, useEffect, useRef } from 'react';
import api from '../../utils/api'; // Assuming you have an API utility
import './Dashboard.scss';

const Dashboard = () => {
  const [dailyData, setDailyData] = useState(null); // State for daily data
  const [error, setError] = useState(null); // State for errors
  const [isLoading, setIsLoading] = useState(false); // State for loading

  // Function to fetch daily data from the API
  const processDailyData = async () => {
    const driver_id = localStorage.getItem('driver_id'); // Get driver_id from localStorage
    setIsLoading(true); // Set loading state while fetching data

    try {
      // Make the API request
      const response = await api.post('/process-daily-data', { driver_id });

      // Log the full response to make sure it's correct
      console.log('Daily Data API Response:', response.data);

      // Ensure that aggregated_data exists and has keys
      if (response.data && response.data.aggregated_data && Object.keys(response.data.aggregated_data).length > 0) {
        const sanitizedAggregatedData = Object.fromEntries(
          Object.entries(response.data.aggregated_data).map(([key, value]) => [
            key,
            isNaN(value) ? 0 : value, // Replace NaN with 0
          ])
        );

        // Update the state with daily data
        setDailyData({
          ...response.data,
          aggregated_data: sanitizedAggregatedData,
        });

        console.log('Sanitized Daily Data:', sanitizedAggregatedData);
      } else {
        setError('No aggregated data found in response');
        console.warn('No aggregated data found in response');
        setDailyData(null);
      }
    } catch (error) {
      setError('Error fetching daily data');
      console.error('Error processing daily data:', error);
      setDailyData(null);
    } finally {
      setIsLoading(false); // Stop loading state after fetch
    }
  };

  // Fetch daily data when the component mounts
  useEffect(() => {
    processDailyData(); // Trigger API call on component mount
  }, []);

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      {/* Error handling */}
      {error && <div className="error-message">{error}</div>}

      {/* Loading state */}
      {isLoading ? (
        <p>Loading daily data...</p>
      ) : (
        dailyData && dailyData.aggregated_data ? (
          <div className="daily-data-section">
            <h3>Daily Driving Data</h3>
            <p><strong>Driving Score:</strong> {dailyData.driving_score}</p>
            <p><strong>Driving Category:</strong> {dailyData.driving_category}</p>
            <ul>
              {/* Loop through the aggregated_data and display each key-value pair */}
              {Object.entries(dailyData.aggregated_data).map(([key, value], index) => (
                <li key={index}>
                  {key}: {isNaN(value) ? 0 : value} {/* Replace NaN with 0 */}
                </li>
              ))}
            </ul>
            <p><strong>Total Distance Covered (km):</strong> {dailyData.total_distance_covered_km}</p>
            <p><strong>Total Trips:</strong> {dailyData.total_trips}</p>
          </div>
        ) : (
          <p>No daily data available yet.</p>
        )
      )}
    </div>
  );
};

export default Dashboard;
