import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet'; 
import 'leaflet/dist/leaflet.css'; 
import L from 'leaflet'; 
import api from '../../utils/api';
import { smartGpsTracking, generateSimulatedRoute } from '../../utils/geolocation';
import BatchProcessing from '../BatchProcessing/BatchProcessing';
import './Dashboard.scss';

// Register Chart.js components
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// Define custom marker icon
const customIcon = new L.Icon({
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [speedData, setSpeedData] = useState([]);
  const [accelerationData, setAccelerationData] = useState([]);
  const [jerkData, setJerkData] = useState([]);
  const [brakingData, setBrakingData] = useState([]);
  const [route, setRoute] = useState([]);
  const [markers, setMarkers] = useState([]);
  const [isTracking, setIsTracking] = useState(false);
  const [error, setError] = useState(null);
  const [dailyData, setDailyData] = useState(null); // New State for daily data
  const [dailyError, setDailyError] = useState(null);
  const [isLoadingDailyData, setIsLoadingDailyData] = useState(false);

  const toggleTracking = () => {
    if (isTracking) {
      setIsTracking(false);
    } else {
      setIsTracking(true);
      startTracking();
    }
  };

  const startTracking = () => {
    const simulatedBatches = generateSimulatedRoute(1, 5);
    simulatedBatches.forEach((batch, index) => {
      setTimeout(() => processBatch(batch), index * 5000);
    });
  };

  const processBatch = async (batch) => {
    const isMoving = smartGpsTracking(batch.gps_data, gpsData);
    if (isMoving) {
      try {
        const driver_id = localStorage.getItem('driver_id');
        const response = await api.post('/record-telematics', {
          driver_id,
          gps_data: batch.gps_data,
        });

        console.log('Telematics Response:', response.data);

        const features = response.data.features;
        if (features) {
          const speed = features['Speed(m/s)'] ?? 0;
          const acceleration = features['Acceleration(m/s^2)'] ?? 0;
          const jerk = features['Jerk(m/s^3)'] ?? 0;
          const brakingIntensity = features['Braking_Intensity'] ?? 0;

          setSpeedData((prevData) => [...prevData, speed]);
          setAccelerationData((prevData) => [...prevData, acceleration]);
          setJerkData((prevData) => [...prevData, jerk]);
          setBrakingData((prevData) => [...prevData, brakingIntensity]);

          setGpsData((prevData) => [...prevData, ...batch.gps_data]);

          const lastPoint = batch.gps_data[batch.gps_data.length - 1];
          setMarkers((prevMarkers) => [
            ...prevMarkers,
            { lat: lastPoint.Latitude, lng: lastPoint.Longitude },
          ]);

          setRoute((prevRoute) => [
            ...prevRoute,
            ...batch.gps_data.map((point) => [point.Latitude, point.Longitude]),
          ]);
        } else {
          console.error('Error: Features object is undefined or missing keys');
        }
      } catch (error) {
        console.error('Error recording telematics data:', error);
        setError('Error processing GPS data. Please try again.');
      }
    }
  };

  // Process Daily Data
  const processDailyData = async () => {
    const driver_id = localStorage.getItem('driver_id');
    setIsLoadingDailyData(true);

    try {
      const response = await api.post('/process-daily-data', { driver_id });

      console.log('Full Daily Data API Response (Stringified):', JSON.stringify(response.data));

      if (response.data && response.data.aggregated_data) {
        console.log('Aggregated Data Found:', response.data.aggregated_data);
        setDailyData(response.data);
        setDailyError(null);
      } else {
        console.warn('No aggregated data found in response. Here is the data:', response.data);
        setDailyError('No aggregated data found in response');
        setDailyData(null);
      }
    } catch (error) {
      console.error('Error processing daily data:', error);
      setDailyError('Error processing daily data');
      setDailyData(null);
    } finally {
      setIsLoadingDailyData(false);
    }
  };

  const renderDailyData = () => {
    if (dailyData) {
      const { aggregated_data, driving_score, driving_category, total_distance_covered_km, total_trips } = dailyData;

      return (
        <div className="daily-data-section">
          <h3>Daily Driving Data</h3>
          <p><strong>Driving Score:</strong> {driving_score}</p>
          <p><strong>Driving Category:</strong> {driving_category}</p>
          <ul>
            {Object.entries(aggregated_data).map(([key, value], index) => (
              <li key={index}>
                {key}: {isNaN(value) ? 0 : value} {/* Replace NaN with 0 */}
              </li>
            ))}
          </ul>
          <p><strong>Total Distance Covered (km):</strong> {total_distance_covered_km}</p>
          <p><strong>Total Trips:</strong> {total_trips}</p>
        </div>
      );
    }

    return <p>No daily data available yet.</p>;
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      <div className="tracking-controls">
        <button onClick={toggleTracking}>
          {isTracking ? 'Stop Tracking' : 'Start Tracking'}
        </button>
        <button onClick={processDailyData} disabled={isLoadingDailyData}>
          {isLoadingDailyData ? 'Processing...' : 'Process Daily Data'}
        </button>
      </div>

      {/* Map Section */}
      <div className="map-section">
        <h3>Route Map</h3>
        <MapContainer center={[26.6337, 92.7926]} zoom={13} scrollWheelZoom={false} className="map-container">
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          <Polyline positions={route} color="blue" />
          {markers.map((marker, idx) => (
            <Marker key={idx} position={[marker.lat, marker.lng]} icon={customIcon}>
              <Popup>
                Latitude: {marker.lat}, Longitude: {marker.lng}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Charts Section */}
      <div className="chart-section">
        <h3>Driving Feature Fluctuations</h3>
        <div className="chart-container">
          {renderChart('Speed (m/s)', speedData, 'rgba(75, 192, 192, 1)')}
          {renderChart('Acceleration (m/s²)', accelerationData, 'rgba(255, 99, 132, 1)')}
          {renderChart('Jerk (m/s³)', jerkData, 'rgba(54, 162, 235, 1)')}
          {renderChart('Braking Intensity', brakingData, 'rgba(153, 102, 255, 1)')}
        </div>
      </div>

      {/* Daily Data Section */}
      {renderDailyData()}

      {/* Error Message */}
      {dailyError && <div className="error">{dailyError}</div>}

      {/* Batch Processing Section */}
      <BatchProcessing gpsData={gpsData} />

      {/* Error Message */}
      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Dashboard;
