import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';  // Importing DailyScorePopup correctly
import { FaTachometerAlt, FaBolt, FaCarCrash, FaMapMarkerAlt } from 'react-icons/fa';
import './Dashboard.scss';

// Importing necessary Chart.js components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [route, setRoute] = useState([]);  // For Polyline in the map
  const [performanceData, setPerformanceData] = useState({
    speed: [],
    acceleration: [],
    jerk: [],
  });
  const [driverId, setDriverId] = useState(null);
  const [error, setError] = useState('');
  const [isMockMode, setIsMockMode] = useState(true);  // Mock GPS for testing

  // Fetch driver data (driver_id) from the backend using JWT token
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/user-data', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDriverId(response.data.driver_id);
      } catch (err) {
        setError('Error fetching user data');
      }
    };
    fetchData();
  }, []);

  // Use effect to handle real-time GPS tracking (mock or real) every 5 seconds
  useEffect(() => {
    if (driverId) {
      const interval = setInterval(() => {
        if (isMockMode) {
          sendMockGpsData();  // Mock GPS data for testing
        } else {
          getRealGpsData();  // Real GPS for mobile devices
        }
      }, 5000);  // Every 5 seconds

      return () => clearInterval(interval);  // Cleanup interval
    }
  }, [driverId, isMockMode]);

  // Send mock GPS data to the backend if speed > 5 km/h
  const sendMockGpsData = async () => {
    const mockGpsData = generateMockGpsData();
    if (mockGpsData.speed > 5) {
      sendGpsDataToBackend([mockGpsData]);
    }
  };

  // Get real GPS data and send to backend when speed > 5 km/h
  const getRealGpsData = () => {
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(
        async (position) => {
          const realGpsData = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            timestamp: Date.now(),
            speed: position.coords.speed ? position.coords.speed * 3.6 : 0,  // Convert to km/h
          };
          if (realGpsData.speed > 5) {
            sendGpsDataToBackend([realGpsData]);
          }
        },
        (err) => setError('Error getting real GPS data: ' + err.message),
        { enableHighAccuracy: true }
      );
    } else {
      setError('Geolocation is not supported by this browser.');
    }
  };

  // Send the batched GPS data to the backend
  const sendGpsDataToBackend = async (gpsDataBatch) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/record-telematics',
        {
          driver_id: driverId,
          gps_data: gpsDataBatch,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      // Update GPS data and performance data on successful response
      const gps = response.data.gps_data;
      const performance = response.data.performance;

      setGpsData(gps);
      setRoute(gps.map((point) => [point.lat, point.lng]));
      setPerformanceData({
        speed: performance.speed,
        acceleration: performance.acceleration,
        jerk: performance.jerk,
      });
    } catch (err) {
      setError('Error sending GPS data');
    }
  };

  // Generate mock GPS data for testing purposes
  const generateMockGpsData = () => {
    const mockRoute = [
      { lat: 51.505, lng: -0.09, timestamp: Date.now(), speed: 10 },  // 10 km/h
      { lat: 51.506, lng: -0.08, timestamp: Date.now() + 5000, speed: 15 },  // 15 km/h
      { lat: 51.507, lng: -0.07, timestamp: Date.now() + 10000, speed: 0 },  // Stopped
    ];
    return mockRoute[Math.floor(Math.random() * mockRoute.length)];
  };

  // Schedule process-daily-data call at 12 AM every day
  useEffect(() => {
    const midnight = new Date();
    midnight.setHours(24, 0, 0, 0);  // Set time to 12 AM midnight

    const delay = midnight.getTime() - Date.now();  // Time until next midnight
    const timer = setTimeout(() => {
      processDailyData();
    }, delay);

    return () => clearTimeout(timer);  // Clean up timer
  }, [driverId]);

  // Process daily data at midnight by calling the backend
  const processDailyData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/process-daily-data',
        {
          driver_id: driverId,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      console.log('Daily data processed', response.data);
    } catch (err) {
      console.error('Error processing daily data', err);
    }
  };

  const chartData = {
    labels: gpsData.map((_, index) => `T${index}`),
    datasets: [
      { label: 'Speed (m/s)', data: performanceData.speed, borderColor: 'rgba(75,192,192,1)' },
      { label: 'Acceleration (m/s²)', data: performanceData.acceleration, borderColor: '#FF6384' },
      { label: 'Jerk (m/s³)', data: performanceData.jerk, borderColor: '#36A2EB' },
    ],
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="dashboard-content">
        <div className="map-section">
          <MapContainer center={[51.505, -0.09]} zoom={13} className="map">
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Polyline positions={route} color="blue" />
          </MapContainer>
        </div>
        <div className="graph-section">
          <Line data={chartData} />
        </div>
      </div>
      <div className="icon-section">
        <div className="icon-item">
          <FaTachometerAlt /> Speed: {performanceData.speed.slice(-1)} m/s
        </div>
        <div className="icon-item">
          <FaBolt /> Acceleration: {performanceData.acceleration.slice(-1)} m/s²
        </div>
        <div className="icon-item">
          <FaCarCrash /> Jerk: {performanceData.jerk.slice(-1)} m/s³
        </div>
        <div className="icon-item">
          <FaMapMarkerAlt /> Total Route Points: {route.length}
        </div>
      </div>
      <DailyScorePopup driverId={driverId} /> {/* Call DailyScorePopup */}
    </div>
  );
};

export default Dashboard;
