// src/components/Dashboard/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import MapView from '../MapView/MapView';
import PerformanceGraph from '../PerformanceGraph/PerformanceGraph';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';
import { trackRealTimeGPS, mockGPSData } from '../../utils/geolocation';
import api from '../../utils/api';

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [showDailyPopup, setShowDailyPopup] = useState(false);
  const [dailyData, setDailyData] = useState(null);

  // Retrieve driver_id from localStorage
  const driverId = localStorage.getItem('driver_id');

  // Fetch mock GPS journey for testing
  useEffect(() => {
    if (driverId) {
      mockGPSData(setGpsData, driverId); // Simulate a journey and batch GPS points
    }
  }, [driverId]);

  // Fetch daily data at midnight
  useEffect(() => {
    const fetchDailyData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await api.post('/process-daily-data', {
          driver_id: driverId
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setDailyData(response.data);
        setShowDailyPopup(true); // Show daily summary popup
      } catch (error) {
        console.error('Error fetching daily data:', error);
      }
    };

    // Schedule daily summary processing at midnight
    const now = new Date();
    const msUntilMidnight = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1).getTime() - now.getTime();

    // Call fetchDailyData at midnight
    const timer = setTimeout(fetchDailyData, msUntilMidnight);
    return () => clearTimeout(timer);
  }, [driverId]);

  return (
    <div className="dashboard">
      <h2>Driver Dashboard</h2>
      <MapView gpsData={gpsData} />  {/* Map with routes */}
      <PerformanceGraph gpsData={gpsData} />  {/* Graph for batch features */}
      {showDailyPopup && <DailyScorePopup data={dailyData} onClose={() => setShowDailyPopup(false)} />}
    </div>
  );
};

export default Dashboard;
