import React, { useState, useEffect } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import api from '../../utils/api';
import { smartGpsTracking, generateSimulatedRoute } from '../../utils/geolocation';
import BatchProcessing from '../BatchProcessing/BatchProcessing';
import './Dashboard.scss';

// Register Chart.js components
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, PointElement, LineElement } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

// Define custom marker icon
const customIcon = new L.Icon({
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [speedData, setSpeedData] = useState([]); 
  const [accelerationData, setAccelerationData] = useState([]); 
  const [jerkData, setJerkData] = useState([]); 
  const [brakingData, setBrakingData] = useState([]); 
  const [route, setRoute] = useState([]); 
  const [markers, setMarkers] = useState([]); 
  const [isTracking, setIsTracking] = useState(false);
  const [dailyData, setDailyData] = useState(null); 
  const [error, setError] = useState(null);

  // Automatically call processDailyData API at midnight
  useEffect(() => {
    const now = new Date();
    const millisUntilMidnight = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1, 0, 0, 0) - now;
    setTimeout(() => {
      processDailyData();
      setInterval(processDailyData, 24 * 60 * 60 * 1000);
    }, millisUntilMidnight);
  }, []);

  // Function to manually call processDailyData API
  const processDailyData = async () => {
    try {
      const driver_id = localStorage.getItem('driver_id');

      if (!driver_id) {
        console.error('Driver ID is missing in localStorage');
        setError('Driver ID is missing.');
        return;
      }

      console.log("Calling process-daily-data API with driver_id:", driver_id);
      const response = await api.post('/process-daily-data', { driver_id });

      if (response && response.data) {
        setDailyData(response.data); // Set the state with fetched data
        console.log('Daily data set successfully:', response.data); 
      } else {
        console.error('No data received from the API');
        setError('No data received from the API');
      }
    } catch (error) {
      console.error('Error fetching daily data:', error.response || error.message);

      if (error.response) {
        setError(`Error: ${error.response.data.error || 'Failed to process daily data'}`);
      } else {
        setError('Network error. Please try again.');
      }
    }
  };

  // Function to start GPS tracking simulation and process GPS data
  const toggleTracking = () => {
    if (isTracking) {
      setIsTracking(false);
    } else {
      setIsTracking(true);
      startTracking();
    }
  };

  const startTracking = () => {
    const simulatedBatches = generateSimulatedRoute(1, 5); 
    simulatedBatches.forEach((batch, index) => {
      setTimeout(() => processBatch(batch), index * 5000); 
    });
  };

  const processBatch = async (batch) => {
    const isMoving = smartGpsTracking(batch.gps_data, gpsData);
    if (isMoving) {
      try {
        const driver_id = localStorage.getItem('driver_id');
        const response = await api.post('/record-telematics', {
          driver_id,
          gps_data: batch.gps_data,
        });

        const features = response.data.features;

        if (features) {
          const speed = features['Speed(m/s)'] ?? 0;
          const acceleration = features['Acceleration(m/s^2)'] ?? 0;
          const jerk = features['Jerk(m/s^3)'] ?? 0;
          const brakingIntensity = features['Braking_Intensity'] ?? 0;

          setSpeedData((prevData) => [...prevData, speed]);
          setAccelerationData((prevData) => [...prevData, acceleration]);
          setJerkData((prevData) => [...prevData, jerk]);
          setBrakingData((prevData) => [...prevData, brakingIntensity]);

          setGpsData((prevData) => [...prevData, ...batch.gps_data]);

          const lastPoint = batch.gps_data[batch.gps_data.length - 1];
          setMarkers((prevMarkers) => [
            ...prevMarkers,
            { lat: lastPoint.Latitude, lng: lastPoint.Longitude },
          ]);

          setRoute((prevRoute) => [
            ...prevRoute,
            ...batch.gps_data.map((point) => [point.Latitude, point.Longitude]),
          ]);
        } else {
          console.error('Error: Features object is undefined or missing keys');
        }
      } catch (error) {
        console.error('Error recording telematics data:', error);
        setError('Error processing GPS data. Please try again.');
      }
    }
  };

  // Render combined distribution graph for daily data
  const renderCombinedChart = () => {
    if (!dailyData || !dailyData.aggregated_data) {
      console.error("Daily data or aggregated_data is missing");
      return null;
    }

    const { Speed, Acceleration, 'Braking_Intensity': brakingIntensity, Jerk, 'Heading_Change(degrees)': headingChange } = dailyData.aggregated_data;

    const data = {
      labels: ['Speed', 'Acceleration', 'Braking Intensity', 'Jerk', 'Heading Change'],
      datasets: [
        {
          label: 'Daily Metrics',
          data: [Speed, Acceleration, brakingIntensity, Jerk || 0, headingChange],
          backgroundColor: [
            'rgba(75, 192, 192, 0.6)',
            'rgba(255, 99, 132, 0.6)',
            'rgba(153, 102, 255, 0.6)',
            'rgba(54, 162, 235, 0.6)',
            'rgba(255, 159, 64, 0.6)',
          ],
          borderColor: [
            'rgba(75, 192, 192, 1)',
            'rgba(255, 99, 132, 1)',
            'rgba(153, 102, 255, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 159, 64, 1)',
          ],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'top',
        },
        title: {
          display: true,
          text: 'Daily Driving Metrics Distribution',
        },
      },
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    };

    return <Bar data={data} options={options} />;
  };

  // Render chart for telematics data (tracking charts)
  const renderTrackingChart = (label, data, borderColor) => {
    const chartLabels = data.map((_, index) => `Batch ${index + 1}`);
    return (
      <Line
        data={{
          labels: chartLabels,
          datasets: [
            {
              label: label,
              data: data,
              borderColor: borderColor,
              fill: false,
            },
          ],
        }}
        options={{
          elements: {
            line: {
              tension: 0.4,
            },
          },
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        }}
      />
    );
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      <div className="tracking-controls">
        <button onClick={toggleTracking}>
          {isTracking ? 'Stop Tracking' : 'Start Tracking'}
        </button>
        <button onClick={processDailyData}>Process Daily Data</button>
      </div>

      {error && <div className="error-message" style={{ color: 'red' }}>{error}</div>}

      {dailyData && (
        <div className="daily-summary">
          <h3>Daily Performance Summary</h3>
          <p>Total Distance Covered: {dailyData.total_distance_covered_km?.toFixed(2) || '0.00'} km</p>
          <p>Total Trips: {dailyData.total_trips || 0}</p>
          <p>Driving Score: {dailyData.driving_score || 'N/A'}</p>
          <p>Driving Category: {dailyData.driving_category || 'N/A'}</p>

          <div className="chart-container">
            {renderCombinedChart()}
          </div>
        </div>
      )}

      <div className="map-section">
        <h3>Route Map</h3>
        <MapContainer center={[26.6337, 92.7926]} zoom={13} scrollWheelZoom={false} className="map-container">
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          <Polyline positions={route} color="blue" />
          {markers.map((marker, idx) => (
            <Marker key={idx} position={[marker.lat, marker.lng]} icon={customIcon}>
              <Popup>
                Latitude: {marker.lat}, Longitude: {marker.lng}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      <div className="chart-section">
        <h3>Driving Feature Fluctuations (Tracking)</h3>
        <div className="chart-container">
          {renderTrackingChart('Speed (m/s)', speedData, 'rgba(75, 192, 192, 1)')}
          {renderTrackingChart('Acceleration (m/s²)', accelerationData, 'rgba(255, 99, 132, 1)')}
          {renderTrackingChart('Jerk (m/s³)', jerkData, 'rgba(54, 162, 235, 1)')}
          {renderTrackingChart('Braking Intensity', brakingData, 'rgba(153, 102, 255, 1)')}
        </div>
      </div>

      <BatchProcessing gpsData={gpsData} />

      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Dashboard;
