import React, { useState, useEffect } from 'react';
import { FaTachometerAlt, FaBolt, FaCarCrash, FaMapMarkerAlt } from 'react-icons/fa';
import axios from 'axios';
import './Dashboard.scss';
import MapView from '../MapView/MapView';
import PerformanceGraph from '../PerformanceGraph/PerformanceGraph';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [route, setRoute] = useState([]);
  const [performanceData, setPerformanceData] = useState({ speed: [], acceleration: [], jerk: [] });
  const [driverId, setDriverId] = useState(null);
  const [error, setError] = useState('');
  const [isMockMode, setIsMockMode] = useState(true);

  // Fetch driver data (driver_id) from the backend using JWT token
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('/api/user-data', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDriverId(response.data.driver_id);
      } catch (err) {
        setError('Error fetching user data');
      }
    };
    fetchData();
  }, []);

  // Batch-based GPS data generation in mock mode or real GPS in mobile mode
  useEffect(() => {
    if (driverId) {
      const interval = setInterval(() => {
        if (isMockMode) {
          sendMockGpsData();
        } else {
          getRealGpsData();
        }
      }, 5000); // Update every 5 seconds

      return () => clearInterval(interval); // Cleanup on unmount
    }
  }, [driverId, isMockMode]);

  // Generate mock GPS data in batches
  const generateMockGpsData = () => {
    const startLat = 40.712776;
    const startLon = -74.005974;
    const timeStepIncrement = 60;
    const distanceIncrement = 0.002;

    const mockData = [];
    for (let i = 0; i < 12; i++) {
      mockData.push({
        Latitude: startLat + i * distanceIncrement,
        Longitude: startLon + i * distanceIncrement,
        Time_Step: 1 + i * timeStepIncrement,
      });
    }

    return mockData;
  };

  // Send mock GPS data
  const sendMockGpsData = async () => {
    const mockGpsData = generateMockGpsData();
    sendGpsDataToBackend(mockGpsData);
  };

  // Get real GPS data
  const getRealGpsData = () => {
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(
        async (position) => {
          const realGpsData = {
            Latitude: position.coords.latitude,
            Longitude: position.coords.longitude,
            Time_Step: Date.now(),
            speed: position.coords.speed ? position.coords.speed * 3.6 : 0,
          };
          if (realGpsData.speed > 5) {
            sendGpsDataToBackend([realGpsData]);
          }
        },
        (err) => setError('Error getting real GPS data: ' + err.message),
        { enableHighAccuracy: true }
      );
    } else {
      setError('Geolocation is not supported by this browser.');
    }
  };

  // Send GPS data to the backend
  const sendGpsDataToBackend = async (gpsDataBatch) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        '/api/record-telematics',
        { driver_id: driverId, gps_data: gpsDataBatch },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const gps = response.data.gps_data || [];
      const performance = response.data.performance || { speed: [], acceleration: [], jerk: [] };

      setGpsData(gps);
      setRoute(gps.map((point) => [point.Latitude, point.Longitude]));
      setPerformanceData({ speed: performance.speed, acceleration: performance.acceleration, jerk: performance.jerk });
    } catch (err) {
      setError('Error sending GPS data');
    }
  };

  // Schedule daily data processing at midnight
  useEffect(() => {
    const midnight = new Date();
    midnight.setHours(24, 0, 0, 0);
    const delay = midnight.getTime() - Date.now();
    const timer = setTimeout(() => {
      processDailyData();
    }, delay);

    return () => clearTimeout(timer); // Cleanup
  }, [driverId]);

  // Process daily data
  const processDailyData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        '/api/process-daily-data',
        { driver_id: driverId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('Daily data processed', response.data);
    } catch (err) {
      console.error('Error processing daily data', err);
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="dashboard-content">
        <MapView route={route} />
        <PerformanceGraph performanceData={performanceData} gpsData={gpsData} />
      </div>
      <div className="icon-section">
        <div className="icon-item">
          <FaTachometerAlt /> Speed: {performanceData?.speed?.slice(-1)} m/s
        </div>
        <div className="icon-item">
          <FaBolt /> Acceleration: {performanceData?.acceleration?.slice(-1)} m/s²
        </div>
        <div className="icon-item">
          <FaCarCrash /> Jerk: {performanceData?.jerk?.slice(-1)} m/s³
        </div>
        <div className="icon-item">
          <FaMapMarkerAlt /> Total Route Points: {route?.length || 0}
        </div>
      </div>
      <DailyScorePopup driverId={driverId} />
    </div>
  );
};

export default Dashboard;
