export const mockGPSData = async (setGpsData, driverId) => {
  // Simulate a "journey" with realistic locations and stops (lat/long of Tezpur, India)
  const journey = [
    // Home to Office
    { Latitude: 26.6338, Longitude: 92.8006, Time_Step: 1 }, // Start (home)
    { Latitude: 26.6348, Longitude: 92.8016, Time_Step: 60 }, // Moving
    { Latitude: 26.6370, Longitude: 92.8050, Time_Step: 120 }, // Near destination
    { Latitude: 26.6382, Longitude: 92.8074, Time_Step: 180 }, // Office (Stop)

    // Office to Market (with a stop at traffic light)
    { Latitude: 26.6382, Longitude: 92.8074, Time_Step: 300 }, // Start from office
    { Latitude: 26.6402, Longitude: 92.8102, Time_Step: 360 }, // Moving
    { Latitude: 26.6415, Longitude: 92.8120, Time_Step: 420 }, // Traffic light stop (10-minute stop)
    { Latitude: 26.6451, Longitude: 92.8185, Time_Step: 900 }, // Market (Stop)

