import React from 'react';
import { Line } from 'react-chartjs-2';
import './PerformanceGraph.scss';

const PerformanceGraph = ({ gpsData }) => {
  const labels = gpsData.map((_, index) => `T${index + 1}`);
  const speeds = gpsData.map(point => point.Speed || 0);
  const accelerations = gpsData.map(point => point.Acceleration || 0);
  const jerks = gpsData.map(point => point.Jerk || 0);

  const data = {
    labels: labels,
    datasets: [
      {
        label: 'Speed (m/s)',
        data: speeds,
        borderColor: 'blue',
        fill: false,
      },
      {
        label: 'Acceleration (m/s²)',
        data: accelerations,
        borderColor: 'red',
        fill: false,
      },
      {
        label: 'Jerk (m/s³)',
        data: jerks,
        borderColor: 'green',
        fill: false,
      },
    ],
  };

  return (
    <div className="performance-graph">
      <h3>Driving Performance</h3>
      <Line data={data} />
    </div>
  );
};

export default PerformanceGraph;
