// src/components/Register/Register.jsx
import React, { useState } from 'react';
import api from '../../utils/api'; // Import the centralized Axios instance
import { useNavigate } from 'react-router-dom';
import './Register.scss';

const Register = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    acceptedTerms: false,
    otp: '',
  });

  const [identityProof, setIdentityProof] = useState(null);
  const [otpSent, setOtpSent] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file.size > 5000000) {
      alert('File size should be less than 5MB');
    } else if (!['image/png', 'image/jpg', 'image/jpeg', 'image/gif'].includes(file.type)) {
      alert('Invalid file type. Only PNG, JPG, JPEG, and GIF are allowed.');
    } else {
      setIdentityProof(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.acceptedTerms) {
      alert('You must accept the terms and conditions.');
      return;
    }

    const formPayload = new FormData();
    formPayload.append('name', formData.name);
    formPayload.append('email', formData.email);
    formPayload.append('password', formData.password);
    formPayload.append('accepted_terms', formData.acceptedTerms);
    formPayload.append('identity_proof', identityProof);

    try {
      setIsSubmitting(true);
      const response = await api.post('/register', formPayload, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (response.status === 201) {
        setOtpSent(true);
        alert('Registration successful! Check your email for the OTP.');
      }
    } catch (error) {
      setError('Registration failed. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await api.post('/verify-otp', {
        email: formData.email,
        otp: formData.otp,
        name: formData.name,
        password: formData.password,
      });

      if (response.status === 200) {
        alert('OTP verified successfully! Redirecting to login.');
        navigate('/login');
      } else {
        setError('Invalid OTP. Please try again.');
      }
    } catch (error) {
      setError('OTP verification failed. Please try again.');
    }
  };

  return (
    <div className="register-container">
      <h2>Register for DriveIQ Telematics</h2>
      {error && <p className="error-message">{error}</p>}

      {!otpSent ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Enter your name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Enter your email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Enter your password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
          <label htmlFor="identity_proof">Upload Identity Proof</label>
          <input
            type="file"
            name="identity_proof"
            accept=".png, .jpg, .jpeg, .gif"
            onChange={handleFileChange}
            required
          />

          <div className="terms-conditions">
            <label>
              <input
                type="checkbox"
                name="acceptedTerms"
                checked={formData.acceptedTerms}
                onChange={(e) => setFormData({ ...formData, acceptedTerms: e.target.checked })}
                required
              />
              I accept the{' '}
              <a href="/terms">terms and conditions</a> of DriveIQ, including GPS tracking for telematics insurance.
            </label>
          </div>

          <button type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Registering...' : 'Register'}
          </button>
        </form>
      ) : (
        <form onSubmit={handleOtpSubmit}>
          <input
            type="text"
            name="otp"
            placeholder="Enter the OTP sent to your email"
            value={formData.otp}
            onChange={handleInputChange}
            required
          />
          <button type="submit">Verify OTP</button>
        </form>
      )}

      <div className="chatbot-icon">
        <a href="/chatbot">Need help? Chat with us!</a>
      </div>

      <div className="no-account">
        <p>Already have an account?</p>
        <a href="/login">Login here</a>
      </div>
    </div>
  );
};

export default Register;
