import React, { useState, useEffect } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import api from '../../utils/api';
import { smartGpsTracking, generateSimulatedRoute } from '../../utils/geolocation';
import BatchProcessing from '../BatchProcessing/BatchProcessing';
import './Dashboard.scss';

// Register Chart.js components
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, PointElement, LineElement } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

const customIcon = new L.Icon({
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [speedData, setSpeedData] = useState([]); // Speed data for tracking
  const [accelerationData, setAccelerationData] = useState([]); // Acceleration data for tracking
  const [jerkData, setJerkData] = useState([]); // Jerk data for tracking
  const [brakingData, setBrakingData] = useState([]); // Braking data for tracking
  const [route, setRoute] = useState([]); // Route lines for the map
  const [markers, setMarkers] = useState([]); // Markers for the map
  const [dailyData, setDailyData] = useState(null); // Daily data from process-daily-data API
  const [error, setError] = useState(null);

 // Function to manually call processDailyData API
 const processDailyData = async () => {
  try {
    const driver_id = localStorage.getItem('driver_id');

    if (!driver_id) {
      setError('Driver ID is missing.');
      return;
    }

    const response = await api.post('/process-daily-data', { driver_id });

    console.log('API response:', response.data);

    if (response.status === 200 && response.data) {
      setDailyData(response.data);  // Set the received data into the state
    } else {
      setError(`Failed to process daily data. Status code: ${response.status}`);
    }
  } catch (error) {
    console.error('Error fetching daily data:', error.message || error);
    setError('Error processing daily data. Please try again.');
  }
};

  // Function to start GPS tracking simulation
  const startTracking = () => {
    const simulatedBatches = generateSimulatedRoute(1, 5); // Generate 5 GPS batches
    simulatedBatches.forEach((batch, index) => {
      setTimeout(() => processBatch(batch), index * 5000); // Process each batch after 5 seconds
    });
  };

  // Function to process GPS batches and record telematics data
  const processBatch = async (batch) => {
    const isMoving = smartGpsTracking(batch.gps_data, gpsData);
    if (isMoving) {
      try {
        const driver_id = localStorage.getItem('driver_id');
        const response = await api.post('/record-telematics', {
          driver_id,
          gps_data: batch.gps_data, // Send GPS data for processing
        });

        const features = response.data.features;

        if (features) {
          const speed = features['Speed(m/s)'] ?? 0;
          const acceleration = features['Acceleration(m/s^2)'] ?? 0;
          const jerk = features['Jerk(m/s^3)'] ?? 0;
          const brakingIntensity = features['Braking_Intensity'] ?? 0;

          // Update data for each chart
          setSpeedData((prevData) => [...prevData, speed]);
          setAccelerationData((prevData) => [...prevData, acceleration]);
          setJerkData((prevData) => [...prevData, jerk]);
          setBrakingData((prevData) => [...prevData, brakingIntensity]);

          // Update GPS data and route points
          setGpsData((prevData) => [...prevData, ...batch.gps_data]);

          // Add only the last point of each batch as a marker
          const lastPoint = batch.gps_data[batch.gps_data.length - 1];
          setMarkers((prevMarkers) => [
            ...prevMarkers,
            { lat: lastPoint.Latitude, lng: lastPoint.Longitude },
          ]);

          setRoute((prevRoute) => [
            ...prevRoute,
            ...batch.gps_data.map((point) => [point.Latitude, point.Longitude]),
          ]);
        }
      } catch (error) {
        console.error('Error processing GPS data:', error);
        setError('Error processing GPS data. Please try again.');
      }
    }
  };

  // Function to render tracking charts
  const renderTrackingChart = (label, data, borderColor) => {
    const chartLabels = data.map((_, index) => `Batch ${index + 1}`);
    return (
      <Line
        data={{
          labels: chartLabels,
          datasets: [
            {
              label: label,
              data: data,
              borderColor: borderColor,
              fill: false,
            },
          ],
        }}
        options={{
          elements: {
            line: {
              tension: 0.4, // Smoother lines for graph
            },
          },
          scales: {
            y: {
              beginAtZero: true, // Start Y axis at zero for clearer data
            },
          },
        }}
      />
    );
  };

  // Render a Bar chart based on aggregated data from the API response
  const renderAggregatedDataChart = () => {
    if (!dailyData || !dailyData.aggregated_data) return null;

    const aggregatedData = dailyData.aggregated_data;

    // Handle NaN values by replacing them with 0
    const safeJerk = isNaN(aggregatedData['Jerk(m/s^3)']) ? 0 : aggregatedData['Jerk(m/s^3)'];

    const data = {
      labels: ['Speed (m/s)', 'Acceleration (m/s²)', 'Braking Intensity', 'Jerk (m/s³)', 'Heading Change (°)'],
      datasets: [
        {
          label: 'Aggregated Data',
          data: [
            aggregatedData['Speed(m/s)'] ?? 0,
            aggregatedData['Acceleration(m/s^2)'] ?? 0,
            aggregatedData['Braking_Intensity'] ?? 0,
            safeJerk,
            aggregatedData['Heading_Change(degrees)'] ?? 0
          ],
          backgroundColor: 'rgba(153, 102, 255, 0.6)',
          borderColor: 'rgba(153, 102, 255, 1)',
          borderWidth: 1,
        },
      ],
    };

    return <Bar data={data} />;
  };

  // Display summary of daily data
  const renderDailyData = () => {
    if (!dailyData) return null;

    // Accessing the correct fields from the API response
    const totalTrips = dailyData.total_trips !== undefined ? dailyData.total_trips : 'N/A';
    const totalDistanceCovered = dailyData.total_distance_covered_km !== undefined
      ? `${dailyData.total_distance_covered_km.toFixed(2)} km`
      : 'N/A';
    const drivingScore = dailyData.driving_score !== undefined ? dailyData.driving_score : 'N/A';
    const drivingCategory = dailyData.driving_category !== undefined ? dailyData.driving_category : 'N/A';

    return (
      <div className="daily-summary-data">
        <h4>Daily Summary</h4>
        <div className="summary-item">
          <span>Total Trips:</span>
          <span>{totalTrips}</span>
        </div>
        <div className="summary-item">
          <span>Total Distance Covered:</span>
          <span>{totalDistanceCovered}</span>
        </div>
        <div className="summary-item">
          <span>Driving Score:</span>
          <span>{drivingScore}</span>
        </div>
        <div className={`summary-item driving-category ${drivingCategory.toLowerCase()}`}>
          <span>Driving Category:</span>
          <span>{drivingCategory}</span>
        </div>
      </div>
    );
  };


  


  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      <div className="tracking-controls">
        <button onClick={startTracking}>Start Tracking</button>
        <button onClick={processDailyData}>Process Daily Data</button>
      </div>

      {/* Error Message Section */}
      {error && <div className="error-message" style={{ color: 'red' }}>{error}</div>}

      {/* Daily Data Section */}
      {dailyData && (
        <div className="daily-summary">
          <h3>Daily Performance Summary</h3>

          {/* Display summary data */}
          {renderDailyData()}

          {/* Render the aggregated data chart */}
          <div className="chart-container">
            {renderAggregatedDataChart()}
          </div>
        </div>
      )}

      {/* Map Section */}
      <div className="map-section">
        <h3>Route Map</h3>
        <MapContainer center={[26.6337, 92.7926]} zoom={13} scrollWheelZoom={false} className="map-container">
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          {/* Display the polyline for the route */}
          <Polyline positions={route} color="blue" />

          {/* Add only the last marker of each batch */}
          {markers.map((marker, idx) => (
            <Marker key={idx} position={[marker.lat, marker.lng]} icon={customIcon}>
              <Popup>
                Latitude: {marker.lat}, Longitude: {marker.lng}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Tracking Data Charts */}
      <div className="chart-section">
        <h3>Driving Feature Fluctuations (Tracking)</h3>
        <div className="chart-container">
          {/* Speed Chart */}
          {renderTrackingChart('Speed (m/s)', speedData, 'rgba(75, 192, 192, 1)')}

          {/* Acceleration Chart */}
          {renderTrackingChart('Acceleration (m/s²)', accelerationData, 'rgba(255, 99, 132, 1)')}

          {/* Jerk Chart */}
          {renderTrackingChart('Jerk (m/s³)', jerkData, 'rgba(54, 162, 235, 1)')}

          {/* Braking Intensity Chart */}
          {renderTrackingChart('Braking Intensity', brakingData, 'rgba(153, 102, 255, 1)')}
        </div>
      </div>

      {/* Batch Processing Section */}
      <BatchProcessing gpsData={gpsData} /> {/* Pass the GPS data to BatchProcessing */}
    </div>
  );
};

export default Dashboard;
