import React from 'react';
import { MapContainer, TileLayer, Polyline, Marker } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css'; // Ensure CSS is imported
import './MapView.scss'; // Ensure custom CSS is imported

// Custom red marker icon
const customMarkerIcon = new L.Icon({
  iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/e/ed/Map_pin_icon.svg',
  iconSize: [25, 40],
  iconAnchor: [12, 40],
});

const MapView = ({ gpsData }) => {
  const initialCoords = [26.6338, 92.8006];  // Default coordinates for Tezpur, India
  
  // If there's no gpsData, fall back to default coordinates
  if (!gpsData || gpsData.length === 0) {
    return <div>No GPS data available</div>;
  }

  // Create a route from the gpsData
  const route = gpsData.map(point => [point.Latitude, point.Longitude]);
  const startPoint = route[0];  // First point in the route
  const endPoint = route[route.length - 1];  // Last point in the route

  return (
    <div className="map-view">
      <MapContainer center={startPoint || initialCoords} zoom={14} className="map">
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        {route.length > 0 && <Polyline positions={route} color="blue" />}
        {startPoint && <Marker position={startPoint} icon={customMarkerIcon} />}
        {endPoint && <Marker position={endPoint} icon={customMarkerIcon} />}
      </MapContainer>
    </div>
  );
};

export default MapView;
