import React, { useState, useEffect } from 'react';
import api from '../../utils/api'; // Axios instance to make API calls
import './DailyScorePopup.scss'; // Importing styles

const DailyScorePopup = ({ driverId }) => {
  const [dailyData, setDailyData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDailyData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await api.post(
          '/process-daily-data',
          { driver_id: driverId },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (response.status === 200) {
          setDailyData(response.data);
        } else {
          setError('No daily data available.');
        }
      } catch (err) {
        setError('Error fetching daily data.');
      }
    };

    fetchDailyData(); // Fetch daily data when component mounts
  }, [driverId]);

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  if (!dailyData) {
    return <p>Loading daily performance...</p>;
  }

  return (
    <div className="daily-score-popup">
      <h3>Daily Performance Summary</h3>
      <div className="summary-details">
        <p><strong>Total Trips:</strong> {dailyData.total_trips}</p>
        <p><strong>Total Distance Covered (km):</strong> {dailyData.total_distance_covered_km.toFixed(2)}</p>
        <p><strong>Average Speed (m/s):</strong> {dailyData.aggregated_data['Speed(m/s)'].toFixed(2)}</p>
        <p><strong>Average Acceleration (m/s²):</strong> {dailyData.aggregated_data['Acceleration(m/s^2)'].toFixed(2)}</p>
        <p><strong>Average Jerk (m/s³):</strong> {dailyData.aggregated_data['Jerk(m/s^3)'].toFixed(2)}</p>
        <p><strong>Driving Score:</strong> {dailyData.driving_score.toFixed(2)}</p>
        <p><strong>Driving Category:</strong> {dailyData.driving_category}</p>
      </div>
    </div>
  );
};

export default DailyScorePopup;
