import React, { useState, useEffect } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet'; // Import Leaflet components
import 'leaflet/dist/leaflet.css'; // Leaflet CSS
import L from 'leaflet'; // Leaflet for custom marker icons
import api from '../../utils/api';
import { smartGpsTracking, generateSimulatedRoute } from '../../utils/geolocation';
import BatchProcessing from '../BatchProcessing/BatchProcessing';
import './Dashboard.scss';

// Register Chart.js components
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, PointElement, LineElement } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

// Define custom marker icon
const customIcon = new L.Icon({
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [route, setRoute] = useState([]);
  const [markers, setMarkers] = useState([]);
  const [isTracking, setIsTracking] = useState(false);
  const [dailyData, setDailyData] = useState(null); // Store daily data
  const [error, setError] = useState(null);

  // Automatically call processDailyData API at midnight
  useEffect(() => {
    const now = new Date();
    const millisUntilMidnight = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1, 0, 0, 0) - now;
    setTimeout(() => {
      processDailyData(); // Call API at midnight
      setInterval(processDailyData, 24 * 60 * 60 * 1000); // Repeat every 24 hours
    }, millisUntilMidnight);
  }, []);

  // Function to manually call processDailyData API
  const processDailyData = async () => {
    try {
      const driver_id = localStorage.getItem('driver_id');

      if (!driver_id) {
        console.error('Driver ID is missing in localStorage');
        setError('Driver ID is missing.');
        return;
      }

      console.log("Calling process-daily-data API with driver_id:", driver_id);

      const response = await api.post('/process-daily-data', { driver_id });

      if (response && response.data) {
        console.log('Daily data fetched successfully:', response.data);
        setDailyData(response.data); // Set the state with fetched data
      } else {
        console.error('No data received from the API');
        setError('No data received from the API');
      }
    } catch (error) {
      console.error('Error fetching daily data:', error.response || error.message);

      if (error.response) {
        setError(`Error: ${error.response.data.error || 'Failed to process daily data'}`);
      } else {
        setError('Network error. Please try again.');
      }
    }
  };

  // Function to start GPS tracking simulation
  const toggleTracking = () => {
    if (isTracking) {
      setIsTracking(false);
    } else {
      setIsTracking(true);
      startTracking();
    }
  };

  const startTracking = () => {
    const simulatedBatches = generateSimulatedRoute(1, 5); // Generate 5 GPS batches
    simulatedBatches.forEach((batch, index) => {
      setTimeout(() => processBatch(batch), index * 5000); // Process each batch after 5 seconds
    });
  };

  const processBatch = async (batch) => {
    const isMoving = smartGpsTracking(batch.gps_data, gpsData);
    if (isMoving) {
      try {
        const driver_id = localStorage.getItem('driver_id');
        const response = await api.post('/record-telematics', {
          driver_id,
          gps_data: batch.gps_data, // Send GPS data for processing
        });

        console.log('Telematics Response:', response.data);

        // Update GPS data and route points
        setGpsData((prevData) => [...prevData, ...batch.gps_data]);

        const lastPoint = batch.gps_data[batch.gps_data.length - 1];
        setMarkers((prevMarkers) => [
          ...prevMarkers,
          { lat: lastPoint.Latitude, lng: lastPoint.Longitude },
        ]);

        setRoute((prevRoute) => [
          ...prevRoute,
          ...batch.gps_data.map((point) => [point.Latitude, point.Longitude]),
        ]);
      } catch (error) {
        console.error('Error recording telematics data:', error);
        setError('Error processing GPS data. Please try again.');
      }
    }
  };

  // Render combined distribution graph for daily data
  const renderCombinedChart = () => {
    if (!dailyData) return null;

    const data = {
      labels: ['Daily Metrics'],
      datasets: [
        {
          label: 'Speed (m/s)',
          data: [dailyData.aggregated_data['Speed(m/s)']],
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
        {
          label: 'Acceleration (m/s²)',
          data: [dailyData.aggregated_data['Acceleration(m/s^2)']],
          backgroundColor: 'rgba(255, 99, 132, 0.6)',
          borderColor: 'rgba(255, 99, 132, 1)',
          borderWidth: 1,
        },
        {
          label: 'Braking Intensity',
          data: [dailyData.aggregated_data['Braking_Intensity']],
          backgroundColor: 'rgba(153, 102, 255, 0.6)',
          borderColor: 'rgba(153, 102, 255, 1)',
          borderWidth: 1,
        },
        {
          label: 'Jerk (m/s³)',
          data: [dailyData.aggregated_data['Jerk(m/s^3)']],
          backgroundColor: 'rgba(54, 162, 235, 0.6)',
          borderColor: 'rgba(54, 162, 235, 1)',
          borderWidth: 1,
        },
        {
          label: 'Heading Change (°)',
          data: [dailyData.aggregated_data['Heading_Change(degrees)']],
          backgroundColor: 'rgba(255, 159, 64, 0.6)',
          borderColor: 'rgba(255, 159, 64, 1)',
          borderWidth: 1,
        },
      ],
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'top',
        },
        title: {
          display: true,
          text: 'Daily Driving Metrics Distribution',
        },
      },
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    };

    return <Bar data={data} options={options} />;
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      <div className="tracking-controls">
        <button onClick={toggleTracking}>
          {isTracking ? 'Stop Tracking' : 'Start Tracking'}
        </button>
        <button onClick={processDailyData}>Process Daily Data</button>
      </div>

      {/* Error Message Section */}
      {error && <div className="error-message" style={{ color: 'red' }}>{error}</div>}

      {/* Daily Data Section */}
      {dailyData && (
        <div className="daily-summary">
          <h3>Daily Performance Summary</h3>
          <p>Total Distance Covered: {dailyData.total_distance_covered_km.toFixed(2)} km</p>
          <p>Total Trips: {dailyData.total_trips}</p>
          <p>Driving Score: {dailyData.driving_score}</p>
          <p>Driving Category: {dailyData.driving_category}</p>

          <div className="chart-container">
            {/* Render combined chart */}
            {renderCombinedChart()}
          </div>
        </div>
      )}

      {/* Map Section */}
      <div className="map-section">
        <h3>Route Map</h3>
        <MapContainer center={[26.6337, 92.7926]} zoom={13} scrollWheelZoom={false} className="map-container">
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          {/* Display the polyline for the route */}
          <Polyline positions={route} color="blue" />

          {/* Add only the last marker of each batch */}
          {markers.map((marker, idx) => (
            <Marker key={idx} position={[marker.lat, marker.lng]} icon={customIcon}>
              <Popup>
                Latitude: {marker.lat}, Longitude: {marker.lng}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Batch Processing Section */}
      <BatchProcessing gpsData={gpsData} /> {/* Pass the GPS data to BatchProcessing */}

      {/* Error Message */}
      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Dashboard;
