// src/components/Dashboard/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import api from '../../utils/api';
import { getCurrentLocation, simulateGpsData, smartGpsTracking, generateSimulatedRoute } from '../../utils/geolocation';
import './Dashboard.scss';
import BatchProcessing from '../BatchProcessing/BatchProcessing';

// Registering Chart.js components for version 3+
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [chartData, setChartData] = useState(null);
  const [route, setRoute] = useState([]);
  const [isTracking, setIsTracking] = useState(false);
  const [error, setError] = useState(null);

  const toggleTracking = () => {
    if (isTracking) {
      setIsTracking(false);
    } else {
      setIsTracking(true);
      startTracking();
    }
  };

  const startTracking = () => {
    const simulatedBatches = generateSimulatedRoute(1, 5);
    simulatedBatches.forEach((batch, index) => {
      setTimeout(() => processBatch(batch), index * 5000);
    });
  };

  const processBatch = async (batch) => {
    const isMoving = smartGpsTracking(batch.gps_data, gpsData);
    if (isMoving) {
      try {
        const driver_id = localStorage.getItem('driver_id');
        const response = await api.post('/record-telematics', {
          driver_id,
          gps_data: batch.gps_data
        });

        console.log('Telematics Response:', response.data);

        const features = response.data.features;

        setGpsData((prevData) => [...prevData, ...batch.gps_data]);
        setRoute((prevRoute) => [
          ...prevRoute,
          ...batch.gps_data.map((point) => [point.Latitude, point.Longitude])
        ]);

        updateChart(features);
      } catch (error) {
        console.error('Error recording telematics data:', error);
        setError('Error processing GPS data. Please try again.');
      }
    }
  };

  const updateChart = (features) => {
    const chartLabels = ['Speed', 'Acceleration', 'Jerk', 'Braking Intensity'];
    setChartData({
      labels: chartLabels,
      datasets: [
        {
          label: 'Driving Features',
          data: [
            features['Speed(m/s)'],
            features['Acceleration(m/s^2)'],
            features['Jerk(m/s^3)'],
            features['Braking_Intensity']
          ],
          borderColor: 'rgba(75, 192, 192, 1)',
          fill: false
        }
      ]
    });
  };

  const renderChart = () => {
    if (!chartData || !chartData.labels || chartData.labels.length === 0) {
      return <p>No data available yet. Start tracking to see the graph.</p>;
    }

    return <Line data={chartData} />;
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      <div className="tracking-controls">
        <button onClick={toggleTracking}>
          {isTracking ? 'Stop Tracking' : 'Start Tracking'}
        </button>
      </div>

      {/* Map Section */}
      <div className="map-section">
        <h3>Route Map</h3>
        <MapContainer center={[26.6337, 92.7926]} zoom={14} scrollWheelZoom={false} className="map-container">
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          <Polyline positions={route} color="blue" />
        </MapContainer>
      </div>

      {/* Chart Section */}
      <div className="chart-section">
        <h3>Driving Feature Fluctuations</h3>
        {renderChart()}  {/* Render chart */}
      </div>

      {/* Error Message */}
      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Dashboard;
