import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Polyline, Marker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import { FaTachometerAlt, FaBolt, FaCarCrash, FaMapMarkerAlt } from 'react-icons/fa';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';
import './Dashboard.scss';

// Import necessary Chart.js components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);   // Store full GPS data for the route
  const [route, setRoute] = useState([]);       // Coordinates for map polyline
  const [performanceData, setPerformanceData] = useState({
    speed: [],
    acceleration: [],
    jerk: [],
  });
  const [driverId, setDriverId] = useState(null);
  const [error, setError] = useState('');
  const [isMoving, setIsMoving] = useState(false); // Track if the car is moving
  const [lastPoint, setLastPoint] = useState(null); // Track the last point of the previous batch
  const [batch, setBatch] = useState([]); // Store current batch data

  // Fetch driver data (driver_id) from the backend using JWT token
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/user-data', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDriverId(response.data.driver_id);
      } catch (err) {
        setError('Error fetching user data');
      }
    };
    fetchData();
  }, []);

  // Track GPS data in real-time
  useEffect(() => {
    if (driverId) {
      const watchId = navigator.geolocation.watchPosition(
        (position) => {
          handlePosition(position.coords.latitude, position.coords.longitude, position.coords.speed);
        },
        (err) => setError('Error getting real GPS data: ' + err.message),
        { enableHighAccuracy: true, maximumAge: 0, timeout: 5000 }
      );
      return () => navigator.geolocation.clearWatch(watchId);
    }
  }, [driverId]);

  // Handle position data
  const handlePosition = (lat, lon, speed) => {
    const newGpsPoint = {
      Latitude: lat,
      Longitude: lon,
      Time_Step: Date.now(),
      Speed: speed ? speed * 3.6 : 0, // Convert to km/h
    };

    if (newGpsPoint.Speed > 5) { // Car is moving
      setIsMoving(true);
      setBatch((prevBatch) => [...prevBatch, newGpsPoint]); // Add new point to the batch
    } else if (isMoving) { // Car has stopped
      setIsMoving(false);
      sendBatchToBackend(batch); // Send batch to backend
      setLastPoint(newGpsPoint); // Set this as the last point for the next batch
      setBatch([]); // Reset the batch for the next set of movements
    }
  };

  // Send batch to the backend
  const sendBatchToBackend = async (gpsDataBatch) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/record-telematics',
        {
          driver_id: driverId,
          gps_data: gpsDataBatch,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const gps = response.data.gps_data || [];
      const performance = response.data.performance || { speed: [], acceleration: [], jerk: [] };

      // Update route with batch start and end points
      if (gpsDataBatch.length > 1) {
        const batchStart = gpsDataBatch[0];
        const batchEnd = gpsDataBatch[gpsDataBatch.length - 1];
        setRoute((prevRoute) => [...prevRoute, [batchStart.Latitude, batchStart.Longitude], [batchEnd.Latitude, batchEnd.Longitude]]);
      }

      // Update performance data
      setPerformanceData({
        speed: performance.speed,
        acceleration: performance.acceleration,
        jerk: performance.jerk,
      });
    } catch (err) {
      console.error('Error sending GPS data:', err);
      setError('Error sending GPS data');
    }
  };

  // Trigger daily data processing at midnight
  useEffect(() => {
    const now = new Date();
    const midnight = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1, 0, 0, 0);
    const msUntilMidnight = midnight.getTime() - now.getTime();

    const timer = setTimeout(() => {
      processDailyData();
    }, msUntilMidnight);

    return () => clearTimeout(timer);
  }, [driverId]);

  const processDailyData = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/process-daily-data',
        { driver_id: driverId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('Daily data processed successfully');
    } catch (err) {
      console.error('Error processing daily data:', err);
    }
  };

  // Chart data
  const chartData = {
    labels: gpsData.map((_, index) => `T${index}`),
    datasets: [
      { label: 'Speed (km/h)', data: performanceData.speed, borderColor: 'rgba(75,192,192,1)' },
      { label: 'Acceleration (m/s²)', data: performanceData.acceleration, borderColor: '#FF6384' },
      { label: 'Jerk (m/s³)', data: performanceData.jerk, borderColor: '#36A2EB' },
    ],
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="dashboard-content">
        <div className="map-section">
          <MapContainer center={[40.712776, -74.005974]} zoom={13} className="map">
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Polyline positions={route} color="blue" />
            {route.length > 0 && <Marker position={route[0]} />}
            {route.length > 1 && <Marker position={route[route.length - 1]} />}
          </MapContainer>
        </div>
        <div className="graph-section">
          <Line data={chartData} />
        </div>
      </div>
      <div className="icon-section">
        <div className="icon-item">
          <FaTachometerAlt /> Speed: {performanceData?.speed?.slice(-1)} km/h
        </div>
        <div className="icon-item">
          <FaBolt /> Acceleration: {performanceData?.acceleration?.slice(-1)} m/s²
        </div>
        <div className="icon-item">
          <FaCarCrash /> Jerk: {performanceData?.jerk?.slice(-1)} m/s³
        </div>
        <div className="icon-item">
          <FaMapMarkerAlt /> Total Route Points: {route.length}
        </div>
      </div>
      <DailyScorePopup driverId={driverId} />
    </div>
  );
};

export default Dashboard;
