import React, { useEffect, useRef } from 'react';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js'; // Explicit imports
import 'chart.js/auto'; // Ensure this is present if needed

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend); // Register required components

const PerformanceGraph = ({ gpsData }) => {
  const chartRef = useRef(null);
  let chartInstance = null;

  const renderChart = () => {
    const ctx = chartRef.current.getContext('2d');

    // Destroy previous chart instance if it exists
    if (chartInstance !== null) {
      chartInstance.destroy();
    }

    chartInstance = new Chart(ctx, {
      type: 'line',
      data: {
        labels: gpsData.map((data, index) => `T-${index + 1}`), // Example labels
        datasets: [{
          label: 'Speed over Time',
          data: gpsData.map(data => data.Speed), // Example data (change as needed)
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 2,
        }],
      },
      options: {
        scales: {
          x: {
            type: 'category', // Category scale for the X-axis
          },
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  };

  useEffect(() => {
    renderChart();
    return () => {
      if (chartInstance) chartInstance.destroy(); // Cleanup on component unmount
    };
  }, [gpsData]); // Re-render the chart when gpsData changes

  return (
    <div className="performance-graph">
      <canvas ref={chartRef}></canvas>
    </div>
  );
};

export default PerformanceGraph;
