// src/utils/geolocation.js

// Get the current location of the user (if using real GPS)
export const getCurrentLocation = () => {
  return new Promise((resolve, reject) => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const timestamp = Math.floor(Date.now() / 1000);
          resolve({ latitude, longitude, timestamp });
        },
        (error) => reject(error),
        { enableHighAccuracy: true, timeout: 5000, maximumAge: 0 }
      );
    } else {
      reject(new Error('Geolocation not supported'));
    }
  });
};

// Simulates gradual acceleration for GPS data
export const generateSimulatedRoute = (driverId, batchSize) => {
  const gpsBatches = [];
  let lat = 26.6337;
  let lon = 92.7926;
  let timeStep = 0;
  let speed = 0; // Initial speed (m/s)
  const maxSpeed = 13.88; // 50 km/h in m/s

  for (let i = 0; i < batchSize; i++) {
    const batch = [];
    for (let j = 0; j < 10; j++) {
      lat += Math.random() * 0.001; // Simulated random movement
      lon += Math.random() * 0.001;
      timeStep += 60;

      // Simulate gradual acceleration for the first few steps, then maintain max speed
      if (speed < maxSpeed) {
        speed += Math.random() * 0.5; // Accelerate gradually (m/s^2)
      } else {
        speed = maxSpeed; // Cap speed to maximum
      }

      batch.push({
        Latitude: lat,
        Longitude: lon,
        Time_Step: timeStep,
        Speed: speed * 3.6 // Convert speed to km/h for backend compatibility
      });
    }
    gpsBatches.push({ driver_id: driverId, gps_data: batch });
  }

  return gpsBatches;
};

// Smart logic for detecting vehicle movement or idle time
export const smartGpsTracking = (currentBatch, previousBatch) => {
  const SPEED_THRESHOLD = 2; // m/s (~7.2 km/h)

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371e3; // Earth radius in meters
    const toRad = (deg) => (deg * Math.PI) / 180;
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c; // Distance in meters
  };

  const calculateSpeed = (distance, timeDiff) => distance / timeDiff;

  if (previousBatch.length > 0) {
    const lastPoint = previousBatch[previousBatch.length - 1];
    const firstPoint = currentBatch[0];
    const distance = calculateDistance(
      lastPoint.Latitude,
      lastPoint.Longitude,
      firstPoint.Latitude,
      firstPoint.Longitude
    );
    const timeDiff = firstPoint.Time_Step - lastPoint.Time_Step;

    if (calculateSpeed(distance, timeDiff) < SPEED_THRESHOLD) {
      console.log('Vehicle idle detected');
      return false;
    }
  }

  return true;
};
