import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Polyline, Marker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import './Dashboard.scss';

// Chart.js setup
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [route, setRoute] = useState([]);
  const [performanceData, setPerformanceData] = useState({
    speed: [],
    acceleration: [],
    jerk: [],
  });
  const [isMoving, setIsMoving] = useState(false);
  const [batch, setBatch] = useState([]);
  const [lastPoint, setLastPoint] = useState(null);
  const [driverId, setDriverId] = useState(null);
  const [error, setError] = useState('');

  // Fetch driver data (e.g., driver ID) from backend
  useEffect(() => {
    const fetchDriverData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/user-data', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDriverId(response.data.driver_id);
      } catch (err) {
        setError('Error fetching user data');
      }
    };
    fetchDriverData();
  }, []);

  // Mock GPS data batch generator
  const generateMockBatchData = () => {
    const distanceIncrement = 0.002;
    const timeStepIncrement = 60; // seconds
    let lastLat = lastPoint?.Latitude || 40.712776; // NYC default starting point
    let lastLon = lastPoint?.Longitude || -74.005974;
    const speedVariation = Math.random() * 0.01; // Small speed variation

    const mockBatch = [];
    for (let i = 0; i < 10; i++) {
      lastLat += distanceIncrement;
      lastLon += distanceIncrement;
      mockBatch.push({
        Latitude: lastLat,
        Longitude: lastLon,
        Time_Step: Date.now() + i * timeStepIncrement * 1000,
        Speed: 8.37 + speedVariation, // Mock speed data
      });
    }

    setLastPoint(mockBatch[mockBatch.length - 1]);
    return mockBatch;
  };

  // Handle GPS data when a new point arrives
  const handlePosition = (lat, lon, speed) => {
    const newGpsPoint = {
      Latitude: lat,
      Longitude: lon,
      Time_Step: Date.now(),
      Speed: speed ? speed * 3.6 : 0, // Convert to km/h
    };

    if (newGpsPoint.Speed > 5) {
      setIsMoving(true);
      setBatch((prevBatch) => [...prevBatch, newGpsPoint]);
    } else if (isMoving) {
      // Car has stopped, send the batch
      setIsMoving(false);
      sendBatchToBackend(batch);
      setLastPoint(newGpsPoint); // Mark this as the last point of the batch
      setBatch([]); // Reset the batch
    }
  };

  // Send batch of GPS data to backend
  const sendBatchToBackend = async (gpsDataBatch) => {
    if (gpsDataBatch.length === 0) return;

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/record-telematics',
        { driver_id: driverId, gps_data: gpsDataBatch },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const gps = response.data.gps_data || [];
      const performance = response.data.performance || { speed: [], acceleration: [], jerk: [] };

      // Map route and performance data
      if (gpsDataBatch.length > 1) {
        const batchStart = gpsDataBatch[0];
        const batchEnd = gpsDataBatch[gpsDataBatch.length - 1];
        setRoute((prevRoute) => [
          ...prevRoute,
          [batchStart.Latitude, batchStart.Longitude],
          [batchEnd.Latitude, batchEnd.Longitude],
        ]);
      }

      setPerformanceData({
        speed: performance.speed,
        acceleration: performance.acceleration,
        jerk: performance.jerk,
      });
    } catch (err) {
      console.error('Error sending GPS data:', err);
      setError('Error sending GPS data');
    }
  };

  // Periodic GPS simulation
  useEffect(() => {
    const interval = setInterval(() => {
      const mockData = generateMockBatchData();
      for (const point of mockData) {
        handlePosition(point.Latitude, point.Longitude, point.Speed);
      }
    }, 10000); // Every 10 seconds for simulation

    return () => clearInterval(interval);
  }, [lastPoint]);

  // Setup chart data
  const chartData = {
    labels: gpsData.map((_, index) => `T${index}`),
    datasets: [
      { label: 'Speed (m/s)', data: performanceData.speed, borderColor: 'rgba(75,192,192,1)' },
      { label: 'Acceleration (m/s²)', data: performanceData.acceleration, borderColor: '#FF6384' },
      { label: 'Jerk (m/s³)', data: performanceData.jerk, borderColor: '#36A2EB' },
    ],
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="dashboard-content">
        <div className="map-section">
          <MapContainer center={[40.712776, -74.005974]} zoom={13} className="map">
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Polyline positions={route} color="blue" />
            {route.length > 0 && <Marker position={route[0]} />}
            {route.length > 1 && <Marker position={route[route.length - 1]} />}
          </MapContainer>
        </div>
        <div className="graph-section">
          <Line data={chartData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
