// src/components/PerformanceGraph/PerformanceGraph.jsx
import React, { useEffect, useRef } from 'react';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import 'chart.js/auto';

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const PerformanceGraph = ({ gpsData }) => {
  const chartRef = useRef(null);
  let chartInstance = null;

  const renderChart = () => {
    const ctx = chartRef.current.getContext('2d');

    // Destroy previous chart instance if it exists
    if (chartInstance !== null) {
      chartInstance.destroy();
    }

    // Ensure valid data before rendering the chart
    if (gpsData.length === 0) {
      return;
    }

    // Extract features from GPS data for plotting
    const labels = gpsData.map((data, index) => `T-${index + 1}`);
    const speeds = gpsData.map(data => data.Speed || 0);  // Fallback to 0 if Speed is undefined
    const accelerations = gpsData.map(data => data.Acceleration || 0);

    chartInstance = new Chart(ctx, {
      type: 'line',
      data: {
        labels,
        datasets: [
          {
            label: 'Speed (m/s)',
            data: speeds,
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 2,
          },
          {
            label: 'Acceleration (m/s²)',
            data: accelerations,
            borderColor: 'rgba(255, 99, 132, 1)',
            borderWidth: 2,
          },
        ],
      },
      options: {
        responsive: true,
        scales: {
          x: {
            type: 'category',
            title: { display: true, text: 'Time Steps' },
          },
          y: {
            beginAtZero: true,
            title: { display: true, text: 'Values' },
          },
        },
      },
    });
  };

  useEffect(() => {
    if (gpsData.length > 0) {
      renderChart();
    }

    return () => {
      if (chartInstance) chartInstance.destroy();
    };
  }, [gpsData]);

  return (
    <div className="performance-graph">
      {gpsData.length > 0 ? <canvas ref={chartRef}></canvas> : <p>No data to display</p>}
    </div>
  );
};

export default PerformanceGraph;
