import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { Line } from 'react-chartjs-2';
import api from '../../utils/api';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';
import { FaTachometerAlt, FaBolt, FaCarCrash, FaMapMarkerAlt } from 'react-icons/fa';
import './Dashboard.scss';

// Importing necessary Chart.js components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [route, setRoute] = useState([]); // For Polyline in the map
  const [performanceData, setPerformanceData] = useState({
    speed: [],
    acceleration: [],
    jerk: [],
  });
  const [driverId, setDriverId] = useState(null);
  const [error, setError] = useState('');
  const [mockMode, setMockMode] = useState(true); // Toggle between mock and real GPS data

  useEffect(() => {
    // Fetch user data on component mount
    const fetchData = async () => {
      try {
        // Get the JWT token from localStorage
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/user-data', {
          headers: {
            Authorization: `Bearer ${token}` // Send the token in the header
          }
        });
        setDriverId(response.data.driver_id);
      } catch (err) {
        setError('Error fetching user data or unauthorized');
      }
    };
    fetchData();
  }, []);


  useEffect(() => {
    if (driverId) {
      if (mockMode) {
        // If in mock mode, simulate GPS data
        const generateMockGpsData = () => {
          let lat = 37.7749;
          let lng = -122.4194;
          const gpsPoints = [];
          const currentTime = new Date().getTime();

          // Generate 10 mock GPS points, spaced by 10 seconds each
          for (let i = 0; i < 10; i++) {
            const timeStep = currentTime + i * 10000; // 10 seconds apart
            gpsPoints.push({
              lat: lat + i * 0.001,
              lng: lng + i * 0.001,
              time_step: timeStep,
            });
          }
          return gpsPoints;
        };

        const gpsPoints = generateMockGpsData();
        sendGpsData(gpsPoints);
      } else {
        // Real GPS tracking using navigator.geolocation
        const fetchRealGpsData = () => {
          if (navigator.geolocation) {
            navigator.geolocation.watchPosition(
              (position) => {
                const gpsPoint = {
                  lat: position.coords.latitude,
                  lng: position.coords.longitude,
                  time_step: new Date().getTime(), // Capture timestamp
                };
                sendGpsData([gpsPoint]); // Send data to backend
              },
              (error) => {
                console.error('Error fetching GPS data:', error);
              },
              { enableHighAccuracy: true, timeout: 5000, maximumAge: 0 }
            );
          }
        };

        fetchRealGpsData();
      }
    }
  }, [driverId, mockMode]);

  // Function to send GPS data to the backend
  const sendGpsData = async (gpsPoints) => {
    try {
      const response = await api.post('/record-telematics', {
        gps_data: gpsPoints,
        driver_id: driverId,
      });

      const gps = gpsPoints;
      const performance = response.data.performance;

      setGpsData(gps);
      setRoute(gps.map((point) => [point.lat, point.lng]));

      setPerformanceData({
        speed: performance.speed,
        acceleration: performance.acceleration,
        jerk: performance.jerk,
      });
    } catch (err) {
      setError('Error sending GPS data');
    }
  };

  const chartData = {
    labels: gpsData.map((_, index) => `T${index}`),
    datasets: [
      {
        label: 'Speed (m/s)',
        data: performanceData.speed,
        fill: false,
        borderColor: 'rgba(75,192,192,1)',
      },
      {
        label: 'Acceleration (m/s²)',
        data: performanceData.acceleration,
        fill: false,
        borderColor: '#FF6384',
      },
      {
        label: 'Jerk (m/s³)',
        data: performanceData.jerk,
        fill: false,
        borderColor: '#36A2EB',
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      {error && <p className="error-message">{error}</p>}

      <div className="dashboard-content">
        {/* Map Section */}
        <div className="map-section">
          <MapContainer center={[51.505, -0.09]} zoom={13} className="map">
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="© OpenStreetMap contributors"
            />
            <Polyline positions={route} color="blue" />
          </MapContainer>
        </div>

        {/* Performance Graph Section */}
        <div className="graph-section">
          <Line data={chartData} />
        </div>
      </div>

      <div className="icon-section">
        <div className="icon-item">
          <FaTachometerAlt /> Speed: {performanceData.speed.slice(-1)} m/s
        </div>
        <div className="icon-item">
          <FaBolt /> Acceleration: {performanceData.acceleration.slice(-1)} m/s²
        </div>
        <div className="icon-item">
          <FaCarCrash /> Jerk: {performanceData.jerk.slice(-1)} m/s³
        </div>
        <div className="icon-item">
          <FaMapMarkerAlt /> Total Route Points: {route.length}
        </div>
      </div>

      {/* Daily Performance Popup */}
      <DailyScorePopup driverId={driverId} />
    </div>
  );
};

export default Dashboard;
