import React, { useState, useEffect } from 'react';
import api from '../../utils/api'; // Ensure your Axios instance is correctly imported

const DailyScorePopup = ({ driverId }) => {
  const [dailyData, setDailyData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDailyData = async () => {
      try {
        const response = await api.post('/process-daily-data', { driver_id: driverId });
        setDailyData(response.data);
      } catch (err) {
        console.error('Error fetching daily data:', err);
        setError('Failed to fetch daily data.');
      } finally {
        setLoading(false);
      }
    };

    fetchDailyData();
  }, [driverId]);

  if (loading) {
    return <div>Loading daily data...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  // Fallback values for missing data to prevent errors
  const totalDistance = dailyData?.total_distance_covered_km || 'N/A';
  const drivingScore = dailyData?.driving_score || 'N/A';
  const drivingCategory = dailyData?.driving_category || 'N/A';

  return (
    <div className="daily-score-popup">
      <h2>Daily Summary</h2>
      <p>Total Distance Covered: {totalDistance} km</p>
      <p>Driving Score: {drivingScore}</p>
      <p>Driving Category: {drivingCategory}</p>
    </div>
  );
};

export default DailyScorePopup;
