// src/utils/api.js
import axios from 'axios';

// Set the base URL for the backend API
const api = axios.create({
  baseURL: 'http://localhost:5000/api', // This should match your Flask backend URL
  headers: {
    'Content-Type': 'application/json',
  },
});

export default api;
