import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DailyScorePopup.scss';

const DailyScorePopup = ({ driverId }) => {
  const [dailyData, setDailyData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDailyData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.post(
          'http://localhost:5000/api/process-daily-data',
          { driver_id: driverId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setDailyData(response.data);
      } catch (err) {
        setError('Error fetching daily data');
      }
    };
    if (driverId) {
      fetchDailyData();
    }
  }, [driverId]);

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="daily-score-popup">
      {dailyData ? (
        <div>
          <h3>Daily Score</h3>
          <p>Driving Score: {dailyData.driving_score}</p>
          <p>Category: {dailyData.driving_category}</p>
          <p>Total Trips: {dailyData.total_trips}</p>
          <p>Total Distance: {dailyData.total_distance_covered_km} km</p>
        </div>
      ) : (
        <p>Loading daily score...</p>
      )}
    </div>
  );
};

export default DailyScorePopup;
