      },
    });
    console.log('Batch successfully sent:', response.data);
  } catch (error) {
    console.error('Error sending GPS data:', error);
  }
};

// Function to generate random time steps between 60 and 300 seconds
const generateRandomTimeStep = () => {
  return Math.floor(Math.random() * (300 - 60 + 1)) + 60; // Random time step between 60 and 300 seconds
};

// Function to generate random lat/long points near a base point
const generateRandomLatLng = (baseLat, baseLng, distance = 0.01) => {
  const randomOffset = () => (Math.random() - 0.5) * distance;  // Generate small random offset for lat/lng
  return {
    Latitude: baseLat + randomOffset(),
    Longitude: baseLng + randomOffset(),
  };
};

// ** 1. Mock GPS Generation: Create Realistic Routes and Break into Smart Batches **
export const mockGPSData = async (setGpsData, driverId) => {
  const basePoints = [
    { Latitude: 26.6338, Longitude: 92.8006 },  // Point A (Home)
    { Latitude: 26.6382, Longitude: 92.8074 },  // Point B (Office)
    { Latitude: 26.6451, Longitude: 92.8185 },  // Point C (Market)
    { Latitude: 26.6492, Longitude: 92.8220 }   // Point D (Pharmacy)
  ];

  const journey = [];

  // Generate random points between A -> B -> C -> D -> A with time steps
  for (let i = 0; i < basePoints.length; i++) {
    const nextPoint = basePoints[(i + 1) % basePoints.length];  // Loop back to A after D
    const currentPoint = basePoints[i];
    let timeStep = generateRandomTimeStep();

    for (let j = 0; j < 5; j++) {  // Create 5 random points between each major stop
      const randomLatLng = generateRandomLatLng(currentPoint.Latitude, currentPoint.Longitude);
      journey.push({ ...randomLatLng, Time_Step: timeStep });
      setGpsData(prev => [...prev, { ...randomLatLng, Time_Step: timeStep }]);  // Update state

      timeStep += generateRandomTimeStep();  // Increment the time step for the next point
    }

    // Add the final point at the destination (B, C, D, A)
    journey.push({ ...nextPoint, Time_Step: timeStep });
    setGpsData(prev => [...prev, { ...nextPoint, Time_Step: timeStep }]);
  }

  // Send GPS data in batches of 5 points
  let batch = [];
  for (let i = 0; i < journey.length; i++) {
    batch.push(journey[i]);

    // Send a batch when there are 5 points or at the end
