// ** 2. Real GPS Tracking: Detect Movement and Batch Data **
export const trackRealTimeGPS = (setGpsData, driverId) => {
  if (!navigator.geolocation) {
    console.error('Geolocation is not supported by your browser');
    return;
  }

  const gpsBatch = [];
  let lastPosition = null;
  let startTime = Date.now();

  const onSuccess = (position) => {
    const { latitude, longitude } = position.coords;
    const timestamp = Date.now();

    // Time step between current and last recorded GPS point
    const timeStep = lastPosition ? calculateTimeStep(startTime, timestamp) : 0;
    startTime = timestamp;

    const gpsDataPoint = {
      Latitude: latitude,
      Longitude: longitude,
      Time_Step: timeStep,  // Time step in seconds
    };

    // If there’s significant movement, record and batch the data
    if (lastPosition) {
      const distanceMoved = calculateDistance(lastPosition, { latitude, longitude }); // in meters

      if (distanceMoved > 10) { // Only track if more than 10m movement
        gpsBatch.push(gpsDataPoint);
        setGpsData((prevGpsData) => [...prevGpsData, gpsDataPoint]);
      }
    } else {
      // First point, just push to the batch
      gpsBatch.push(gpsDataPoint);
    }

    // Send batch every 5 points or at significant stop
    if (gpsBatch.length >= 5 || timeStep > 300) {  // Send on stop or batch size
      sendGPSBatchToBackend(gpsBatch, driverId);
      gpsBatch.length = 0;  // Reset the batch
    }

    lastPosition = { latitude, longitude };
  };

  const onError = (error) => {
    console.error(`Error fetching geolocation: ${error.message}`);
  };

  const options = {
    enableHighAccuracy: true,
    timeout: 5000,
    maximumAge: 0,
  };

  navigator.geolocation.watchPosition(onSuccess, onError, options);
};

// Helper function to calculate distance between two GPS points (in meters)
const calculateDistance = (point1, point2) => {
  const R = 6371e3;  // Radius of the earth in meters
  const lat1 = point1.latitude * Math.PI / 180;
  const lat2 = point2.latitude * Math.PI / 180;
  const deltaLat = (point2.latitude - point1.latitude) * Math.PI / 180;
  const deltaLon = (point2.longitude - point1.longitude) * Math.PI / 180;

  const a = Math.sin(deltaLat / 2) * Math.sin(deltaLat / 2) +
            Math.cos(lat1) * Math.cos(lat2) *
            Math.sin(deltaLon / 2) * Math.sin(deltaLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;  // Returns the distance in meters
};

// Helper function to calculate time step between two timestamps (in seconds)
const calculateTimeStep = (startTime, endTime) => {
  return Math.floor((endTime - startTime) / 1000);  // Return difference in seconds
};
