import React, { useState, useEffect } from 'react';
import MapView from '../MapView/MapView';
import PerformanceGraph from '../PerformanceGraph/PerformanceGraph';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';
import { trackRealTimeGPS, mockGPSData } from '../../utils/geolocation';
import api from '../../utils/api';

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);  // Store GPS data in state
  const [showDailyPopup, setShowDailyPopup] = useState(false);  // Popup control
  const [dailyData, setDailyData] = useState(null);  // Store daily data

  // Retrieve driver_id from localStorage
  const driverId = localStorage.getItem('driver_id');

  // Fetch mock GPS journey for testing
  useEffect(() => {
    if (driverId) {
      mockGPSData(setGpsData, driverId);  // Simulate a GPS journey
    }
  }, [driverId]);

  // Fetch daily data (this should run at midnight to collect the day's performance)
  useEffect(() => {
    const fetchDailyData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await api.post('/process-daily-data', {
          driver_id: driverId
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setDailyData(response.data);
        setShowDailyPopup(true);  // Show daily summary popup
      } catch (error) {
        console.error('Error fetching daily data:', error);
      }
    };

    // Simulate daily data fetching at midnight
    const timer = setTimeout(fetchDailyData, 3000);  // Simulate 3s delay for daily summary
    return () => clearTimeout(timer);
  }, [driverId]);

  return (
    <div className="dashboard">
      <h2>Driver Dashboard</h2>
      {/* Map and Graph components receive gpsData */}
      <MapView gpsData={gpsData} />
      <PerformanceGraph gpsData={gpsData} />
      {showDailyPopup && <DailyScorePopup data={dailyData} onClose={() => setShowDailyPopup(false)} />}
    </div>
  );
};

export default Dashboard;
