import React from 'react';
import { MapContainer, TileLayer, Polyline, Marker } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './MapView.scss';

// Custom red marker icon for start/end points
const customMarkerIcon = new L.Icon({
  iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/e/ed/Map_pin_icon.svg',
  iconSize: [25, 40], // Size of the icon
  iconAnchor: [12, 40], // Point where the marker anchors on the map
});

const MapView = ({ gpsData }) => {
  const initialCoords = [26.6338, 92.8006];  // Coordinates for Tezpur, India (center point)
  
  // Create route from gpsData
  const route = gpsData.map(point => [point.Latitude, point.Longitude]);
  const startPoint = route[0];
  const endPoint = route[route.length - 1];

  return (
    <div className="map-view">
      <MapContainer center={startPoint || initialCoords} zoom={14} className="map">
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* Draw the route */}
        {route.length > 0 && <Polyline positions={route} color="blue" />}

        {/* Add start and end markers */}
        {startPoint && <Marker position={startPoint} icon={customMarkerIcon} />}
        {endPoint && <Marker position={endPoint} icon={customMarkerIcon} />}
      </MapContainer>
    </div>
  );
};

export default MapView;
