import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import api from '../../utils/api';
import { smartGpsTracking, generateSimulatedRoute } from '../../utils/geolocation';
import BatchProcessing from '../BatchProcessing/BatchProcessing';
import './Dashboard.scss';

// Chart.js components
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// Custom marker icon for the map
const customIcon = new L.Icon({
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
});

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [speedData, setSpeedData] = useState([]);
  const [accelerationData, setAccelerationData] = useState([]);
  const [jerkData, setJerkData] = useState([]);
  const [brakingData, setBrakingData] = useState([]);
  const [route, setRoute] = useState([]);
  const [markers, setMarkers] = useState([]);
  const [isTracking, setIsTracking] = useState(false);
  const [dailyData, setDailyData] = useState(null);
  const [error, setError] = useState(null);

  // Automatically call processDailyData API at midnight
  useEffect(() => {
    const now = new Date();
    const millisUntilMidnight = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1, 0, 0, 0) - now;
    setTimeout(() => {
      processDailyData(); // Call API at midnight
      setInterval(processDailyData, 24 * 60 * 60 * 1000); // Repeat every 24 hours
    }, millisUntilMidnight);
  }, []);

  // Function to manually call processDailyData API
  const processDailyData = async () => {
    try {
      const driver_id = localStorage.getItem('driver_id');

      if (!driver_id) {
        console.error('Driver ID is missing in localStorage');
        setError('Driver ID is missing.');
        return;
      }

      console.log("Calling process-daily-data API with driver_id:", driver_id);

      const response = await api.post('/process-daily-data', { driver_id });

      if (response && response.data) {
        console.log('Daily data fetched successfully:', response.data);
        setDailyData(response.data);  // Set the state with fetched data
      } else {
        console.error('No data received from the API');
        setError('No data received from the API');
      }
    } catch (error) {
      console.error('Error fetching daily data:', error.response || error.message);

      if (error.response) {
        setError(`Error: ${error.response.data.error || 'Failed to process daily data'}`);
      } else {
        setError('Network error. Please try again.');
      }
    }
  };

  // Function to start GPS tracking simulation
  const toggleTracking = () => {
    setIsTracking(!isTracking);
    if (!isTracking) startTracking();
  };

  const startTracking = () => {
    const simulatedBatches = generateSimulatedRoute(1, 5); // Simulates 5 batches of GPS data
    simulatedBatches.forEach((batch, index) => {
      setTimeout(() => processBatch(batch), index * 5000);
    });
  };

  // Process each batch of GPS data
  const processBatch = async (batch) => {
    const isMoving = smartGpsTracking(batch.gps_data, gpsData);
    if (isMoving) {
      try {
        const driver_id = localStorage.getItem('driver_id');
        const response = await api.post('/record-telematics', {
          driver_id,
          gps_data: batch.gps_data,
        });

        const features = response.data.features;
        if (features) {
          const speed = features['Speed(m/s)'] * 3.6 ?? 0; // Convert m/s to km/h
          const acceleration = features['Acceleration(m/s^2)'] ?? 0;
          const jerk = features['Jerk(m/s^3)'] ?? 0;
          const brakingIntensity = features['Braking_Intensity'] ?? 0;

          // Update states
          setSpeedData((prevData) => [...prevData, speed]);
          setAccelerationData((prevData) => [...prevData, acceleration]);
          setJerkData((prevData) => [...prevData, jerk]);
          setBrakingData((prevData) => [...prevData, brakingIntensity]);

          setGpsData((prevData) => [...prevData, ...batch.gps_data]);

          const lastPoint = batch.gps_data[batch.gps_data.length - 1];
          setMarkers((prevMarkers) => [
            ...prevMarkers,
            { lat: lastPoint.Latitude, lng: lastPoint.Longitude },
          ]);

          setRoute((prevRoute) => [
            ...prevRoute,
            ...batch.gps_data.map((point) => [point.Latitude, point.Longitude]),
          ]);
        }
      } catch (error) {
        console.error('Error recording telematics data:', error);
        setError('Error processing GPS data. Please try again.');
      }
    }
  };

  // Function to render charts for the features
  const renderChart = (label, data, borderColor) => {
    return (
      <Line
        data={{
          labels: ['Daily'], // Label for daily data
          datasets: [
            {
              label: label,
              data: data,
              borderColor: borderColor,
              fill: true,
              backgroundColor: 'rgba(52, 152, 219, 0.1)',
              pointRadius: 5,
              pointBackgroundColor: '#fff',
              pointBorderColor: borderColor,
              pointHoverRadius: 7,
            },
          ],
        }}
        options={{
          elements: {
            line: {
              tension: 0.4, // Smoother lines for graph
            },
          },
          scales: {
            y: {
              beginAtZero: true, // Start Y axis at zero for clearer data
            },
          },
        }}
      />
    );
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>

      <div className="tracking-controls">
        <button onClick={toggleTracking}>
          {isTracking ? 'Stop Tracking' : 'Start Tracking'}
        </button>
        <button onClick={processDailyData}>Process Daily Data</button>
      </div>

      {/* Error Message Section */}
      {error && <div className="error-message" style={{ color: 'red' }}>{error}</div>}

      {/* Daily Data Section */}
      {dailyData && (
        <div className="daily-summary">
          <h3>Daily Performance Summary</h3>
          <p>Total Distance Covered: {dailyData.total_distance_covered_km.toFixed(2)} km</p>
          <p>Total Trips: {dailyData.total_trips}</p>
          <p>Driving Score: {dailyData.driving_score}</p>
          <p>Driving Category: {dailyData.driving_category}</p>

          <div className="chart-container">
            {/* Rendering charts for the features using the daily data */}
            {renderChart('Speed (km/h)', [dailyData.aggregated_data['Speed(km/h)']], 'rgba(75, 192, 192, 1)')}
            {renderChart('Acceleration (m/s²)', [dailyData.aggregated_data['Acceleration(m/s^2)']], 'rgba(255, 99, 132, 1)')}
            {renderChart('Jerk (m/s³)', [dailyData.aggregated_data['Jerk(m/s^3)']], 'rgba(54, 162, 235, 1)')}
            {renderChart('Braking Intensity', [dailyData.aggregated_data['Braking_Intensity']], 'rgba(153, 102, 255, 1)')}
          </div>
        </div>
      )}

      {/* Map Section */}
      <div className="map-section">
        <h3>Route Map</h3>
        <MapContainer center={[26.6337, 92.7926]} zoom={13} scrollWheelZoom={false} className="map-container">
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OpenStreetMap contributors" />
          <Polyline positions={route} color="blue" weight={5} dashArray={[10, 20]} lineJoin="round" />
          {markers.map((marker, idx) => (
            <Marker key={idx} position={[marker.lat, marker.lng]} icon={customIcon}>
              <Popup>Latitude: {marker.lat}, Longitude: {marker.lng}</Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Batch Processing Section */}
      <BatchProcessing gpsData={gpsData} />
    </div>
  );
};

export default Dashboard;
