import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import DailyScorePopup from '../DailyScorePopup/DailyScorePopup';
import { FaTachometerAlt, FaBolt, FaCarCrash, FaMapMarkerAlt } from 'react-icons/fa';
import './Dashboard.scss';

// Importing necessary Chart.js components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the Chart.js components
ChartJS.register(
  CategoryScale, // This registers the 'category' scale
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [gpsData, setGpsData] = useState([]);
  const [route, setRoute] = useState([]); // For Polyline in the map
  const [performanceData, setPerformanceData] = useState({
    speed: [],
    acceleration: [],
    jerk: [],
  });
  const [driverId, setDriverId] = useState(null);
  const [error, setError] = useState('');
  const [isMockMode, setIsMockMode] = useState(true);  // Toggle between mock GPS and real GPS

  useEffect(() => {
    // Fetch driver data and get the driver_id
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/user-data', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setDriverId(response.data.driver_id);
      } catch (err) {
        setError('Error fetching user data');
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (driverId) {
      const interval = setInterval(() => {
        if (isMockMode) {
          sendMockGpsData();
        } else {
          getRealGpsData();
        }
      }, 5000); // Every 5 seconds

      return () => clearInterval(interval); // Clean up interval
    }
  }, [driverId, isMockMode]);

  // Send mock GPS data to the backend
  const sendMockGpsData = async () => {
    const mockGpsData = generateMockGpsData();
    sendGpsDataToBackend(mockGpsData);
  };

  // Send real GPS data to the backend
  const getRealGpsData = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        const realGpsData = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
          timestamp: Date.now()  // Numeric timestamp in milliseconds
        };
        sendGpsDataToBackend(realGpsData);
      });
    } else {
      setError('Geolocation is not supported on this device.');
    }
  };

  // Send GPS data to the backend
  const sendGpsDataToBackend = async (gpsDataPoint) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/record-telematics', {
        driver_id: driverId,
        gps_data: [gpsDataPoint],  // Send as array of one object
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Process the response
      const gps = response.data.gps_data;
      const performance = response.data.performance;
      setGpsData(gps);
      setRoute(gps.map((point) => [point.lat, point.lng]));
      setPerformanceData({
        speed: performance.speed,
        acceleration: performance.acceleration,
        jerk: performance.jerk,
      });
    } catch (err) {
      setError('Error sending GPS data');
    }
  };

  // Generate mock GPS data for testing purposes
  const generateMockGpsData = () => {
    const mockRoute = [
      { lat: 51.505, lng: -0.09, timestamp: Date.now() },
      { lat: 51.506, lng: -0.08, timestamp: Date.now() + 5000 },
      { lat: 51.507, lng: -0.07, timestamp: Date.now() + 10000 },
    ];
    return mockRoute[Math.floor(Math.random() * mockRoute.length)];
  };

  const chartData = {
    labels: gpsData.map((_, index) => `T${index}`),
    datasets: [
      { label: 'Speed (m/s)', data: performanceData.speed, borderColor: 'rgba(75,192,192,1)' },
      { label: 'Acceleration (m/s²)', data: performanceData.acceleration, borderColor: '#FF6384' },
      { label: 'Jerk (m/s³)', data: performanceData.jerk, borderColor: '#36A2EB' },
    ],
  };

  return (
    <div className="dashboard-container">
      <h2>Driver Dashboard</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="dashboard-content">
        <div className="map-section">
          <MapContainer center={[51.505, -0.09]} zoom={13} className="map">
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Polyline positions={route} color="blue" />
          </MapContainer>
        </div>
        <div className="graph-section">
          <Line data={chartData} />
        </div>
      </div>
      <div className="icon-section">
        <div className="icon-item">
          <FaTachometerAlt /> Speed: {performanceData.speed.slice(-1)} m/s
        </div>
        <div className="icon-item">
          <FaBolt /> Acceleration: {performanceData.acceleration.slice(-1)} m/s²
        </div>
        <div className="icon-item">
          <FaCarCrash /> Jerk: {performanceData.jerk.slice(-1)} m/s³
        </div>
        <div className="icon-item">
          <FaMapMarkerAlt /> Total Route Points: {route.length}
        </div>
      </div>
      <DailyScorePopup driverId={driverId} />
    </div>
  );
};

export default Dashboard;
